class RecipeNCFDataset(Dataset):
    def __init__(self, dataframe):
        required_cols = ['user_id', 'recipe_id', 'label']
        for col in required_cols:
            if col not in dataframe.columns:
                raise ValueError(f"Missing column: {col}")

        self.user_ids = torch.tensor(dataframe['user_id'].values, dtype=torch.long)
        self.recipe_ids = torch.tensor(dataframe['recipe_id'].values, dtype=torch.long)
        self.labels = torch.tensor(dataframe['label'].values, dtype=torch.float32)

        print(f"âœ… Dataset: {len(self)} samples")
        print(f"   Positive: {self.labels.mean()*100:.1f}%")

    def __len__(self):
        return len(self.user_ids)

    def __getitem__(self, idx):
        return {
            'user_id': self.user_ids[idx],
            'recipe_id': self.recipe_ids[idx],
            'label': self.labels[idx]
        }

    def get_num_users(self):
        return self.user_ids.max().item() + 1

    def get_num_recipes(self):
        return self.recipe_ids.max().item() + 1

